import React, { useState } from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
// Remove unused imports for images
// import dumbbellImage from "../assets/dumbbell.png";
// import barImage from "../assets/bar.png";
import "./Table.css";

const makeStyle = (status) => {
  // Function implementation
};

const BasicTable = ({ rows = [], handleUpdateOrder, handleDeleteOrder }) => {
  const [selectedRow, setSelectedRow] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [details, setDetails] = useState("");
  const [modalOpen, setModalOpen] = useState(false);

  const handleUpdate = (name) => {
    handleUpdateOrder(name, { imageUrl, details });
    setModalOpen(false);
  };

  const handleDelete = (name) => {
    handleDeleteOrder(name);
    setModalOpen(false);
  };

  const handleRowClick = (name, imageUrl) => {
    setSelectedRow(name);
    setImageUrl(imageUrl);
    setModalOpen(true);
  };

  const handleImageUrlChange = (e) => {
    setImageUrl(e.target.value);
  };

  const handleDetailsChange = (e) => {
    setDetails(e.target.value);
  };

  return (
    <div className="Table">
      <h3>Recent Gym Orders</h3>
      <TableContainer
        component={Paper}
        style={{
          boxShadow: "0px 13px 20px 0px #80808029",
          maxHeight: "400px",
          overflowY: "scroll",
        }}
      >
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Product</TableCell>
              <TableCell align="left">Tracking ID</TableCell>
              <TableCell align="left">Date</TableCell>
              <TableCell align="left">Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody style={{ color: "white" }}>
            {rows.map((row) => (
              <TableRow
                key={row.name}
                sx={{
                  "&:last-child td, &:last-child th": { border: 0 },
                  cursor: "pointer",
                  backgroundColor:
                    selectedRow === row.name ? "lightgray" : "inherit",
                }}
                onClick={() => handleRowClick(row.name, row.imageUrl)}
              >
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell align="left">{row.trackingId}</TableCell>
                <TableCell align="left">{row.date}</TableCell>
                <TableCell align="left">
                  <span className="status" style={makeStyle(row.status)}>
                    {row.status}
                  </span>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      {modalOpen && (
        <div className="Modal">
          <h3>Update Details for {selectedRow}</h3>
          <div>
            <img src={imageUrl} alt="Product" style={{ width: "100px" }} />
          </div>
          <div>
            <label>Image URL:</label>
            <input
              type="text"
              value={imageUrl}
              onChange={handleImageUrlChange}
            />
          </div>
          <div>
            <label>Details:</label>
            <textarea value={details} onChange={handleDetailsChange} />
          </div>
          <div>
            <button onClick={() => handleUpdate(selectedRow)}>Update</button>
            <button onClick={() => handleDelete(selectedRow)}>Delete</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default BasicTable;
