import React, { useState } from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import Modal from "@mui/material/Modal";
import "./Table.css";

const descriptions = {
  Dumbbells: "Description for Dumbbells",
  "Gym Bar": "Description for Gym Bar",
};

const makeStyle = (status) => {
  if (status === "Approved") {
    return {
      background: "rgb(145 254 159 / 47%)",
      color: "green",
    };
  } else if (status === "Pending") {
    return {
      background: "#ffadad8f",
      color: "red",
    };
  } else {
    return {
      background: "#59bfff",
      color: "white",
    };
  }
};

const BasicTable = ({ handleUpdateOrder, handleDeleteOrder }) => {
  const [selectedRow, setSelectedRow] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [modalOpen, setModalOpen] = useState(false);
  const [newImageUrl, setNewImageUrl] = useState("");
  const [file, setFile] = useState(null);
  const [description, setDescription] = useState("");
  const [rows, setRows] = useState([
    {
      name: "Dumbbells",
      trackingId: 12345,
      date: "2024-05-26",
      status: "Approved",
      imageUrl: "dumbbellImage",
    },
    {
      name: "Gym Bar",
      trackingId: 67890,
      date: "2024-05-25",
      status: "Pending",
      imageUrl: "barImage",
    },
  ]);

  const [creationModalOpen, setCreationModalOpen] = useState(false);
  const [newProductName, setNewProductName] = useState("");
  const [newProductImage, setNewProductImage] = useState("");
  const [newProductDescription, setNewProductDescription] = useState("");

  const handleRowClick = (name, imageUrl) => {
    setSelectedRow(name);
    setImageUrl(imageUrl);
    setNewImageUrl("");
    setDescription(descriptions[name]);
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
    if (file && newImageUrl) {
      handleUpdate();
    }
  };

  const handleCreateOrder = () => {
    const newOrder = {
      name: newProductName,
      trackingId: Math.floor(Math.random() * 100000),
      date: new Date().toISOString().split("T")[0],
      status: "Pending",
      imageUrl: newProductImage,
    };
    setRows([...rows, newOrder]);

    setNewProductName("");
    setNewProductImage("");
    setNewProductDescription("");
    setCreationModalOpen(false);
  };

  const handleUpdate = () => {
    if (file) {
      const updatedRows = rows.map((row) => {
        if (row.name === selectedRow) {
          return {
            ...row,
            imageUrl: newImageUrl || imageUrl,
            description: description,
          };
        }
        return row;
      });
      setRows(updatedRows);
      console.log("Image updated:", newImageUrl || imageUrl); // Log the updated image URL
      setFile(null); // Reset file state
      setNewImageUrl(""); // Reset new image URL
      setDescription(""); // Reset description
      setModalOpen(false); // Close the modal after updating
    } else {
      // Prompt user to select a file
      const input = document.createElement("input");
      input.type = "file";
      input.accept = "image/*";
      input.onchange = (e) => {
        const selectedFile = e.target.files[0];
        const reader = new FileReader();
        reader.onload = (e) => {
          setNewImageUrl(e.target.result);
          setFile(selectedFile);
        };
        reader.readAsDataURL(selectedFile);
      };
      input.click();
    }
  };

  return (
    <div className="Table">
      <h3>Recent Gym Orders</h3>
      <button onClick={() => setCreationModalOpen(true)}>
        Create New Order
      </button>
      <TableContainer
        component={Paper}
        style={{
          boxShadow: "0px 13px 20px 0px #80808029",
          maxHeight: "400px",
          overflowY: "scroll",
        }}
      >
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Product</TableCell>
              <TableCell align="left">Tracking ID</TableCell>
              <TableCell align="left">Date</TableCell>
              <TableCell align="left">Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody style={{ color: "white" }}>
            {rows.map((row) => (
              <TableRow
                key={row.name}
                sx={{
                  "&:last-child td, &:last-child th": { border: 0 },
                  cursor: "pointer",
                  backgroundColor:
                    selectedRow === row.name ? "lightgray" : "inherit",
                }}
                onClick={() => handleRowClick(row.name, row.imageUrl)}
              >
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell align="left">{row.trackingId}</TableCell>
                <TableCell align="left">{row.date}</TableCell>
                <TableCell align="left">
                  <span className="status" style={makeStyle(row.status)}>
                    {row.status}
                  </span>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Modal open={modalOpen} onClose={handleCloseModal}>
        {/* Modal content for existing orders */}
      </Modal>
      <Modal
        open={creationModalOpen}
        onClose={() => setCreationModalOpen(false)}
      >
        <div className="Modal" style={{ textAlign: "center" }}>
          <h3>Create New Order</h3>
          <input
            type="text"
            placeholder="Product Name"
            value={newProductName}
            onChange={(e) => setNewProductName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Product Image URL"
            value={newProductImage}
            onChange={(e) => setNewProductImage(e.target.value)}
          />
          <input
            type="text"
            placeholder="Product Description"
            value={newProductDescription}
            onChange={(e) => setNewProductDescription(e.target.value)}
          />
          <button onClick={handleCreateOrder}>Create</button>
        </div>
      </Modal>
    </div>
  );
};

export default BasicTable;
