import React, { useState } from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import dumbbellImage from "../assets/dumbbell.png";
import barImage from "../assets/bar.png";
import "./Table.css";

function createData(name, trackingId, date, status) {
  return { name, trackingId, date, status };
}

const rows = [
  createData("Dumbbells", 18232908424, "25 May 2024", "Approved"),
  createData("Gym Bar", 18934325424, "25 May 2024", "Pending"),
];

const makeStyle = (status) => {
  if (status === "Approved") {
    return {
      background: "rgb(145 254 159 / 47%)",
      color: "green",
    };
  } else if (status === "Pending") {
    return {
      background: "#ffadad8f",
      color: "red",
    };
  } else {
    return {
      background: "#59bfff",
      color: "white",
    };
  }
};

const BasicTable = ({ handleUpdateOrder, handleDeleteOrder }) => {
  const [selectedRow, setSelectedRow] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [details, setDetails] = useState("");

  const handleUpdate = (name) => {
    handleUpdateOrder(name, { imageUrl, details });
    setSelectedRow(null);
    setImageUrl("");
    setDetails("");
  };

  const handleDelete = (name) => {
    handleDeleteOrder(name);
    setSelectedRow(null);
  };

  const handleRowClick = (name) => {
    setSelectedRow(name);
    const selectedRowData = rows.find((row) => row.name === name);
    setImageUrl(selectedRowData.imageUrl || "");
    setDetails(selectedRowData.details || "");
  };

  const handleImageUrlChange = (e) => {
    setImageUrl(e.target.value);
  };

  const handleDetailsChange = (e) => {
    setDetails(e.target.value);
  };

  return (
    <div className="Table">
      <h3>Recent Gym Orders</h3>
      <TableContainer
        component={Paper}
        style={{
          boxShadow: "0px 13px 20px 0px #80808029",
          maxHeight: "400px",
          overflowY: "scroll",
        }}
      >
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Product</TableCell>
              <TableCell align="left">Tracking ID</TableCell>
              <TableCell align="left">Date</TableCell>
              <TableCell align="left">Status</TableCell>
              <TableCell align="left">Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody style={{ color: "white" }}>
            {rows.map((row) => (
              <TableRow
                key={row.name}
                sx={{
                  "&:last-child td, &:last-child th": { border: 0 },
                  cursor: "pointer",
                  backgroundColor:
                    selectedRow === row.name ? "lightgray" : "inherit",
                }}
                onClick={() => handleRowClick(row.name)}
              >
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell align="left">{row.trackingId}</TableCell>
                <TableCell align="left">{row.date}</TableCell>
                <TableCell align="left">
                  <span className="status" style={makeStyle(row.status)}>
                    {row.status}
                  </span>
                </TableCell>
                <TableCell align="left" className="Actions">
                  <button onClick={() => handleUpdate(row.name)}>Update</button>
                  <button onClick={() => handleDelete(row.name)}>Delete</button>
                </TableCell>
                <TableCell align="left" className="Details">
                  {row.name === "Dumbbells" ? (
                    <img
                      src={dumbbellImage}
                      alt="Dumbbells"
                      style={{ width: "50px" }}
                    />
                  ) : (
                    <img
                      src={barImage}
                      alt="Gym Bars"
                      style={{ width: "50px" }}
                    />
                  )}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      {selectedRow && (
        <div className="Modal">
          <h3>Update Details for {selectedRow}</h3>
          <div>
            <label>Image URL:</label>
            <input
              type="text"
              value={imageUrl}
              onChange={handleImageUrlChange}
            />
          </div>
          <div>
            <label>Details:</label>
            <textarea value={details} onChange={handleDetailsChange} />
          </div>
        </div>
      )}
    </div>
  );
};

export default BasicTable;
