import React, { useState } from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import Modal from "@mui/material/Modal";
import dumbbellImage from "../assets/dumbbell.png";
import barImage from "../assets/bar.png";
import "./Table.css";

const makeStyle = (status) => {
  if (status === "Approved") {
    return {
      background: "rgb(145 254 159 / 47%)",
      color: "green",
    };
  } else if (status === "Pending") {
    return {
      background: "#ffadad8f",
      color: "red",
    };
  } else {
    return {
      background: "#59bfff",
      color: "white",
    };
  }
};

const descriptions = {
  Dumbbells:
    "Dumbbells are versatile fitness tools that can be used for a wide range of exercises targeting various muscle groups.",
  "Gym Bar":
    "A gym bar, also known as a weightlifting bar or barbell, is used for performing compound exercises like squats, deadlifts, and bench presses.",
};

const BasicTable = ({ handleUpdateOrder, handleDeleteOrder }) => {
  const [selectedRow, setSelectedRow] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [modalOpen, setModalOpen] = useState(false);
  const [newImageUrl, setNewImageUrl] = useState("");
  const [file, setFile] = useState(null);
  const [description, setDescription] = useState("");
  const [newDescription, setNewDescription] = useState("");
  const [rows, setRows] = useState([
    {
      name: "Dumbbells",
      trackingId: 12345,
      date: "2024-05-26",
      status: "Approved",
      imageUrl: dumbbellImage,
    },
    {
      name: "Gym Bar",
      trackingId: 67890,
      date: "2024-05-25",
      status: "Pending",
      imageUrl: barImage,
    },
  ]);

  const handleRowClick = (name, imageUrl) => {
    setSelectedRow(name);
    setImageUrl(imageUrl);
    setNewImageUrl(""); // Reset new image URL
    setDescription(descriptions[name]);
    setNewDescription(descriptions[name]); // Reset new description
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
    if (file && newImageUrl) {
      handleUpdate();
    }
  };

  const handleUpdate = () => {
    if (file) {
      const updatedRows = rows.map((row) => {
        if (row.name === selectedRow) {
          return {
            ...row,
            imageUrl: newImageUrl || imageUrl,
            description: newDescription || description,
          };
        }
        return row;
      });
      setRows(updatedRows); // Update rows with the new image URL and description
      console.log("Image updated:", newImageUrl || imageUrl); // Log the updated image URL
      console.log("Description updated:", newDescription || description); // Log the updated description
      setFile(null); // Reset file state
      setNewImageUrl(""); // Reset new image URL
      setNewDescription(""); // Reset new description
    } else {
      // Prompt user to select a file
      const input = document.createElement("input");
      input.type = "file";
      input.accept = "image/*";
      input.onchange = (e) => {
        const selectedFile = e.target.files[0];
        const reader = new FileReader();
        reader.onload = (e) => {
          setNewImageUrl(e.target.result);
          setFile(selectedFile);
        };
        reader.readAsDataURL(selectedFile);
      };
      input.click();
    }
  };

  const handleDelete = () => {
    // Implement delete functionality here
    console.log("Delete button clicked");
  };

  return (
    <div className="Table">
      <h3>Recent Gym Orders</h3>
      <TableContainer
        component={Paper}
        style={{
          boxShadow: "0px 13px 20px 0px #80808029",
          maxHeight: "400px",
          overflowY: "scroll",
        }}
      >
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Product</TableCell>
              <TableCell align="left">Tracking ID</TableCell>
              <TableCell align="left">Date</TableCell>
              <TableCell align="left">Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody style={{ color: "white" }}>
            {rows.map((row) => (
              <TableRow
                key={row.name}
                sx={{
                  "&:last-child td, &:last-child th": { border: 0 },
                  cursor: "pointer",
                  backgroundColor:
                    selectedRow === row.name ? "lightgray" : "inherit",
                }}
                onClick={() => handleRowClick(row.name, row.imageUrl)}
              >
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell align="left">{row.trackingId}</TableCell>
                <TableCell align="left">{row.date}</TableCell>
                <TableCell align="left">
                  <span className="status" style={makeStyle(row.status)}>
                    {row.status}
                  </span>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Modal open={modalOpen} onClose={handleCloseModal}>
        <div className="Modal" style={{ textAlign: "center" }}>
          <h3>{selectedRow}</h3>
          <img
            src={newImageUrl || imageUrl}
            alt={selectedRow}
            style={{
              maxWidth: "100%",
              maxHeight: "100%",
              margin: "auto",
              display: "block",
              border: "none",
            }}
          />
          <textarea
            value={newDescription || description}
            onChange={(e) => setNewDescription(e.target.value)}
            rows={4}
            style={{ width: "100%", marginTop: 10 }}
          />
          <button onClick={handleUpdate}>Update</button>
          <button onClick={handleDelete}>Delete</button>
        </div>
      </Modal>
    </div>
  );
};

export default BasicTable;
