import React, { useState } from "react";
import "./Sidebar.css";
import Logo from "../imgs/logo.png";
import { UilSignOutAlt } from "@iconscout/react-unicons";
import { SidebarData } from "../Data/Data";
import { UilBars } from "@iconscout/react-unicons";
import { motion } from "framer-motion";
import { Link, useHistory } from "react-router-dom"; // Import Link and useHistory

const Sidebar = () => {
  const [selected, setSelected] = useState(0);
  const [expanded, setExpaned] = useState(true);
  const history = useHistory(); // Get history object from React Router

  const sidebarVariants = {
    true: {
      left: "0",
    },
    false: {
      left: "-60%",
    },
  };

  const handleItemClick = (index, path) => {
    setSelected(index);
    history.push(path); // Redirect to the specified path
  };

  return (
    <>
      <div
        className="bars"
        style={expanded ? { left: "60%" } : { left: "5%" }}
        onClick={() => setExpaned(!expanded)}
      >
        <UilBars />
      </div>
      <motion.div
        className="sidebar"
        variants={sidebarVariants}
        animate={window.innerWidth <= 768 ? `${expanded}` : ""}
      >
        {/* logo */}
        <div className="logo">
          <img src={Logo} alt="logo" />
          <span>
            GY<span>M</span>REPS
          </span>
        </div>
        <div className="menu">
          {SidebarData.map((item, index) => (
            <div
              key={index}
              onClick={() => handleItemClick(index, item.path)} // Call handleItemClick with index and path
              className={selected === index ? "menuItem active" : "menuItem"}
            >
              <div className="menuItem">
                <item.icon />
                <span>{item.heading}</span>
              </div>
            </div>
          ))}
          {/* signoutIcon */}
          <div className="menuItem">
            <UilSignOutAlt />
          </div>
        </div>
      </motion.div>
    </>
  );
};

export default Sidebar;
