import React, { useState } from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import dumbbellImage from "../assets/dumbbell.png";
import barImage from "../assets/bar.png";
import "./Table.css";

function createData(name, trackingId, date, status) {
  return { name, trackingId, date, status };
}

const makeStyle = (status) => {
  if (status === "Approved") {
    return {
      background: "rgb(145 254 159 / 47%)",
      color: "green",
    };
  } else if (status === "Pending") {
    return {
      background: "#ffadad8f",
      color: "red",
    };
  } else {
    return {
      background: "#59bfff",
      color: "white",
    };
  }
};

const BasicTable = ({ rows, handleUpdateOrder, handleDeleteOrder }) => {
  const [selectedRow, setSelectedRow] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [details, setDetails] = useState("");

  const handleUpdate = (name) => {
    handleUpdateOrder(name, { imageUrl, details });
    setSelectedRow(null);
    setImageUrl("");
    setDetails("");
  };

  const handleDelete = (name) => {
    handleDeleteOrder(name);
    setSelectedRow(null);
  };

  const handleImageClick = (row) => {
    setSelectedRow(row);
  };

  return (
    <div className="Table">
      <h3>Recent Gym Orders</h3>
      <TableContainer
        component={Paper}
        style={{
          boxShadow: "0px 13px 20px 0px #80808029",
          maxHeight: "400px",
          overflowY: "scroll",
        }}
      >
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Product</TableCell>
              <TableCell align="left">Tracking ID</TableCell>
              <TableCell align="left">Date</TableCell>
              <TableCell align="left">Status</TableCell>
              <TableCell align="left">Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody style={{ color: "white" }}>
            {rows.map((row) => (
              <TableRow
                key={row.name}
                sx={{
                  "&:last-child td, &:last-child th": { border: 0 },
                  cursor: "pointer",
                  backgroundColor:
                    selectedRow === row.name ? "lightgray" : "inherit",
                }}
              >
                <TableCell component="th" scope="row">
                  <button
                    onClick={() => handleImageClick(row)}
                    style={{
                      background: "none",
                      border: "none",
                      padding: 0,
                      cursor: "pointer",
                    }}
                  >
                    {row.name === "Dumbbells" ? (
                      <img
                        src={dumbbellImage}
                        alt="Dumbbells"
                        style={{ width: "50px" }}
                      />
                    ) : (
                      <img
                        src={barImage}
                        alt="Gym Bars"
                        style={{ width: "50px" }}
                      />
                    )}
                  </button>
                </TableCell>
                <TableCell align="left">{row.trackingId}</TableCell>
                <TableCell align="left">{row.date}</TableCell>
                <TableCell align="left">
                  <span className="status" style={makeStyle(row.status)}>
                    {row.status}
                  </span>
                </TableCell>
                <TableCell align="left" className="Actions">
                  <button onClick={() => handleUpdate(row.name)}>Update</button>
                  <button onClick={() => handleDelete(row.name)}>Delete</button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      {selectedRow && (
        <div className="Modal">
          <h3>Update Details for {selectedRow.name}</h3>
          <div>
            <label>Image URL:</label>
            <input
              type="text"
              value={imageUrl}
              onChange={(e) => setImageUrl(e.target.value)}
            />
          </div>
          <div>
            <label>Details:</label>
            <textarea
              value={details}
              onChange={(e) => setDetails(e.target.value)}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default BasicTable;
