import React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import dumbbellImage from "../assets/dumbbell.png";
import barImage from "../assets/bar.png";
import "./Table.css";

const makeStyle = (status) => {
  if (status === "Approved") {
    return {
      background: "rgb(145 254 159 / 47%)",
      color: "green",
    };
  } else if (status === "Pending") {
    return {
      background: "#ffadad8f",
      color: "red",
    };
  } else {
    return {
      background: "#59bfff",
      color: "white",
    };
  }
};

const BasicTable = ({
  rows,
  handleUpdateOrder,
  handleDeleteOrder,
  handleUpdateOrderDetails,
}) => {
  const handleUpdate = (name) => {
    // Here you can implement logic to update the order details
    // For demonstration purposes, let's assume we are updating the status
    handleUpdateOrderDetails(name, { status: "Updated" });
  };

  const handleDelete = (name) => {
    // Call the handleDeleteOrder function with the name of the order to delete
    handleDeleteOrder(name);
  };

  return (
    <div className="Table">
      <h3>Recent Gym Orders</h3>
      <TableContainer
        component={Paper}
        style={{
          boxShadow: "0px 13px 20px 0px #80808029",
          maxHeight: "400px",
          overflowY: "scroll",
        }}
      >
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Product</TableCell>
              <TableCell align="left">Tracking ID</TableCell>
              <TableCell align="left">Date</TableCell>
              <TableCell align="left">Status</TableCell>
              <TableCell align="left">Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody style={{ color: "white" }}>
            {rows.map((row) => (
              <TableRow
                key={row.name}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell align="left">{row.trackingId}</TableCell>
                <TableCell align="left">{row.date}</TableCell>
                <TableCell align="left">
                  <span className="status" style={makeStyle(row.status)}>
                    {row.status}
                  </span>
                </TableCell>
                <TableCell align="left" className="Actions">
                  {/* Buttons for updating and deleting orders */}
                  <button onClick={() => handleUpdate(row.name)}>Update</button>
                  <button onClick={() => handleDelete(row.name)}>Delete</button>
                </TableCell>
                <TableCell align="left" className="Details">
                  {row.name === "Dumbbells" ? (
                    <img
                      src={dumbbellImage}
                      alt="Dumbbells"
                      style={{ width: "50px" }}
                    />
                  ) : (
                    <img
                      src={barImage}
                      alt="Gym Bars"
                      style={{ width: "50px" }}
                    />
                  )}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default BasicTable;
